package com.patientdocconnect.model;

import com.patientdocconnect.model.Email;
import com.patientdocconnect.model.EmailBuilder;

import org.junit.Test;
import static org.junit.Assert.*;

public class EmailTest {
  private static String PATH_TO_CONFIG = "src/main/resources/email_config.ini";
  private static String TO = "neomol@protonmail.com";
  private static String SUBJECT = "prueba";
  private static String CONTENT = ":D";
  private static Email email;

  @Test
  public void testBuildingEmail() {
    EmailBuilder builder = new EmailBuilder();
    email = builder.build(PATH_TO_CONFIG);

    assertFalse("Make sure we have a valid email instance", email == null);
  }

  @Test
  public void testSendingEmail() {
    assertFalse("Make sure we have a valid email instance", email == null);
    assertTrue("Ensure that we can send emails", email.sendEmail(TO, SUBJECT, CONTENT));
  }
}
